import { Avatar } from "@material-ui/core";
import React, { useState, useEffect, useContext } from "react";
import InputEmoji from "react-input-emoji";
import { Link } from "react-router-dom";
import Usercontext from "../context/user";
import { getAllPostComment, getIndivisualPost } from "../services/firebase";
import firebase from "firebase";
import {
  Comment,
  Favorite,
  FavoriteBorder,
  OpenInNew,
} from "@material-ui/icons";
import {
  FacebookIcon,
  FacebookShareButton,
  LinkedinIcon,
  LinkedinShareButton,
  WhatsappIcon,
  WhatsappShareButton,
} from "react-share";

function PostPopup({ postId }) {
  const user = useContext(Usercontext);

  const [comment, setComment] = useState("");
  const [postComment, setPostComment] = useState([]);
  const [post, setPost] = useState(null);

  useEffect(() => {
    async function getMyPost() {
      const postComments = await getAllPostComment(postId);
      const response = await getIndivisualPost(postId);

      await response.onSnapshot((snapshot) => {
        setPost(snapshot.data());
      });

      await postComments.onSnapshot((snapshot) => {
        setPostComment(
          snapshot.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        );
      });
    }
    if (postId !== "") {
      getMyPost();
    }
  }, [postId]);

  const Liked = async (t) => {
    if (t) {
      const likeditem = post.liked;
      var index = likeditem.indexOf(user.user.uid);
      if (index > -1) {
        likeditem.splice(index, 1);
      }
      await firebase.firestore().collection("photos").doc(postId).update({
        liked: likeditem,
      });
    } else {
      await firebase
        .firestore()
        .collection("photos")
        .doc(postId)
        .update({
          liked: [...post.liked, user.user.uid],
        });
    }
  };

  const PostComment = async (e) => {
    // e.preventDefault();
    const comments = [
      ...postComment,
      {
        comment: comment,
        commentUser: user.user.username,
        timestamp: firebase.firestore.FieldValue.serverTimestamp(),
        commentUserUid: user.user.uid,
      },
    ];

    await firebase
      .firestore()
      .collection("photos")
      .doc(postId)
      .collection("comments")
      .add({
        comment: comment,
        commentUser: user.user.displayName,
        timestamp: firebase.firestore.FieldValue.serverTimestamp(),
        commentUserUid: user.user.uid,
      });

    // setThisImageComment(comments);

    setComment("");
  };

  // if (post && user) {
  //   if (post.liked.includes(user.user.uid)) {
  //     setLike(true);
  //   } else {
  //     setLike(false);
  //   }
  // }

  return post ? (
    <div style={{ display: "grid", gridTemplateColumns: "60% 40%" }}>
      <div
        style={{ borderRight: "1px solid gray", backgroundColor: "#343835" }}
      >
      {post.type === 'video' ? <h1>video</h1>: <img
          src={post.fileUrl}
          alt="post Image"
          style={{
            width: "100%",
            height: "89vh",
            border: "1px solid black",

            // position: "relative",

            objectFit: "contain",
          }}
        />}
        
      </div>
      <div>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            margin: "10px",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            {/* <Avatar /> */}
            <div style={{ marginLeft: "10px" }}>
              <Link
                to={`/profile/${post.user_uid}`}
                style={{ textDecoration: "none" }}
              >
                <h5>{post.username}</h5>
              </Link>

              <p style={{ fontSize: "10px", color: "gray" }}>
                {post.timestamp.toDate().toDateString()}
              </p>
            </div>
          </div>
          <Link
            to={`/profile/${post.user_uid}`}
            style={{ textDecoration: "none" }}
          >
            Profile
          </Link>
        </div>
        <div>
          <p
            style={{
              borderBottom: "1px solid black",
              fontSize: "14px",
              paddingLeft: "10px",
              paddingBottom: "10px",
            }}
          >
            <b>caption: </b>
            {post.caption}
            <span
              style={{ color: "gray", fontSize: "13px", marginLeft: "6px" }}
            >
              {post.caption === "Update my Profile Pic at"
                ? post.timestamp.toDate().toDateString()
                : null}
            </span>
          </p>
        </div>
        <div>
          <div
            style={{
              margin: "10px 0px 0px 10px",
              overflow: "scroll",
              overflowX: "hidden",
              height: "55vh",
            }}
          >
            {postComment.map(({ data, id }) => (
              <div
                style={{
                  display: "flex",
                  // alignItems: "center",
                  margin: "5px 7px 9px 5px ",
                }}
                key={id}
              >
                <p>
                  <span
                    style={{
                      color: "#610909",
                      fontSize: "15px",
                      fontWeight: "bold",
                      marginRight: "5px",
                    }}
                  >
                    <Link
                      to={`/profile/${data.commentUserUid}`}
                      style={{ textDecoration: "none" }}
                    >
                      {data ? data.commentUser : null} :
                    </Link>
                  </span>
                  <span style={{ fontSize: "15px", color: "#4a4747" }}>
                    {data ? data.comment : null}{" "}
                  </span>
                  <span style={{ color: "gray", fontSize: "10px" }}>
                    {data
                      ? data.timestamp
                        ? data.timestamp.toDate().toDateString()
                        : null
                      : null}
                  </span>
                </p>
              </div>
            ))}
          </div>
        </div>

        <div
          style={{
            position: "fixed",
            bottom: "0px",
            width: "32vw",
            backgroundColor: "#c7c7c7",
            borderTop: "2px solid gray",
          }}
        >
          <div>
            {" "}
            {user ? (
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                  margin: "10px 10px 0 10px",
                }}
              >
                <div>
                  {post.liked.includes(user.user.uid) ? (
                    <Favorite
                      style={{
                        marginLeft: "10px",
                        marginRight: "10px",
                        color: "red",
                        cursor: "pointer",
                      }}
                      onClick={() => Liked(true)}
                    />
                  ) : (
                    <FavoriteBorder
                      style={{
                        marginLeft: "10px",
                        marginRight: "10px",
                        cursor: "pointer",
                      }}
                      onClick={() => Liked(false)}
                    />
                  )}

                  <Comment />
                  <Link style={{ marginLeft: "10px" }} to={`/image/${postId}`}>
                    <OpenInNew />
                  </Link>
                </div>
                <div>
                  <FacebookShareButton
                    url={`${window.location.href}image/${postId}`}
                    // quote={props.joke.setup + props.joke.punchline}
                    hashtag="#StupidProgrammer"
                  >
                    <FacebookIcon size={25} round={true} />
                  </FacebookShareButton>
                  <WhatsappShareButton
                    url={`${window.location.href}image/${postId}`}
                    // quote={props.joke.setup + props.joke.punchline}
                    hashtag="#StupidProgrammer"
                  >
                    <WhatsappIcon
                      size={25}
                      round={true}
                      style={{ marginLeft: "5px", marginRight: "5px" }}
                    />
                  </WhatsappShareButton>
                  <LinkedinShareButton
                    url={`${window.location.href}image/${postId}`}
                    // quote={props.joke.setup + props.joke.punchline}
                    hashtag="#StupidProgrammer"
                  >
                    <LinkedinIcon size={25} round={true} />
                  </LinkedinShareButton>
                </div>
              </div>
            ) : null}
            <p
              style={{
                marginLeft: "10px",
                color: "gray",
                marginTop: "-5px",
                fontSize: "10px",
              }}
            >
              {post.liked.length} likes {postComment.length} comments
            </p>
          </div>
          <InputEmoji
            maxLength={200}
            style={{ width: "100vw", backgroundColor: "gray" }}
            value={comment}
            onChange={setComment}
            cleanOnEnter
            onEnter={PostComment}
            placeholder="Type a message"
          />
        </div>
      </div>
    </div>
  ):null
}

export default PostPopup;
